import React,{useState} from 'react'
import './App.css';
import axios from "axios"
import {useHistory} from "react-router-dom"

export default function Login({setLoginUser}) {
   

   const history =useHistory()

    const [user, setUser] = useState({
        name: "",
        password:"",
 });

   const handleChange = e =>{
      const {name,value} = e.target
      console.log(name, value)
      setUser({...user,[name]: value})
   }        
    const login =() =>{
       axios.post("http://localhost:9002/Login",user)
       .then(res => {
          alert(res.data.message)
         setLoginUser(res.data.message)
         history.push("/")
      })
    }
  return (
      <>
      <div className="sidenav">
         <div className="login-main-text">
            <h2>Addministrator<br/> Login Page.</h2>
    
         </div>
      </div>
      <div className="main">

         <div className="col-md-6 col-sm-12">
             <h1> Hospital Management System.</h1>
            <div className="login-form">
            
               <form>
                  <div className="form-group">
                     {console.log("user", user)}
                     <label>User Name</label>
                     <input type="text" name="name" className="form-control" placeholder="User Name" value={user.name}
                     onChange={handleChange}/>
                  </div>
                  <div className="form-group">
                     <label>Password</label>
                     <input type="password" name = "password" className="form-control" placeholder="Password" value={user.password}
                     onChange={handleChange}/>
                  </div>
                  <button type="submit" className="btn btn-black" onClick={login}>Login</button>&nbsp;&nbsp;&nbsp;
                  <button type="submit" className="btn btn-secondary" onClick={() => history.push("/register")}>Register</button>
               </form>
            </div>
         </div>
      </div>
      </>
  )
}
